/* eslint valid-jsdoc: "off" */

'use strict';
require('dotenv').config();

/**
 * @param {Egg.EggAppInfo} appInfo app info
 */
module.exports = appInfo => {
    /**
     * built-in config
     * @type {Egg.EggAppConfig}
     **/
    const config = exports = {};

    // use for cookie sign key, should change to your own and keep security
    config.keys = appInfo.name + '_1666719296467_4527';

    // add your middleware config here
    config.middleware = ['auth', 'graphql'];


    // graphql
    config.graphql = {
        // 默认访问路由
        // http://127.0.0.1:7001/graphql
        router: '/graphql',
        //   是否加载到 app 上，默认开启
        app: true,
        // 是否加载到 agent 上，默认关闭
        agent: false,
        // 是否加载开发者工具 graphiql, 默认开启。路由同 router 字段。使用浏览器打开该可见。
        graphiql: true,
        // graphQL 路由前的拦截器
        // onPreGraphQL: function* (ctx) {
        // },
        // 开发工具 graphiQL 路由前的拦截器，建议用于做权限操作(如只提供开发者使用)
        // onPreGraphiQL: function* (ctx) {
        // },
    };


    config.mongoose = {
        url: process.env.MONGOOSE_URL, // 端口号27021数据库名VietNamVisa
        options: { useNewUrlParser: true, useUnifiedTopology: true }, // 其他配置警告解除方法
    };


    config.redis = {
        client: {
            port: 6379, // Redis port
            host: '192.168.101.3', // Redis host
            password: '',
            db: 0,
        },
    };


    config.cors = {
        origin: '*', // 跨任何域
        allowMethods: 'GET,HEAD,PUT,POST,DELETE,PATCH,OPTIONS', // 被允许的请求方式
    };

    config.jwt = {
        expire: 7200, // 2小时
        secret: process.env.JWT_SECRET,
        refresh_expire: 259200, // 3天
        refresh_secret: process.env.REFRESH_SECRET,
        ignore: ['/api/registered', '/api/login'], // 哪些请求不需要认证
        // expiresIn: '24h',
    };


    exports.security = {
        csrf: {
            // 当支付宝异步通知当前服务器时忽略csrf验证
            ignore: ctx => {
                // console.log("____________________________________________________");
                // console.log(ctx.request.url);
                if (ctx.request.url === '/aliPay/aliPayNotify' || ctx.request.url === '/graphql' || ctx.request.url === '/graphql?' || ctx.request.url === '/api/registered' || ctx.request.url === '/api/login' || ctx.request.url === '/webhook') {
                    return true;
                }
                return false;
            },
        },
    };

    // 阿里云配置
    config.ali = {
        accessKeyId: process.env.ALI_ACCESS_KEY_ID,
        accessKeySecret: process.env.ALI_ACCESS_KEY_SECRET,
        endpoint: 'https://dysmsapi.aliyuncs.com',
        apiVersion: '2017-05-25',
    };

    // 支付宝支付配置
    exports.aliPayOptions = {
        app_id: '2021000121657681',
        appPrivKeyFile: '',
        alipayPubKeyFile: '',
    };


    // 支付宝支付回调地址
    exports.aliPayBasicParams = {
        // 支付成功返回地址
        return_url: process.env.ALIPAY_RETURN_URL,
        // 支付成功异步通知地址
        notify_url: process.env.ALIPAY_NOTIFY_URL,
    };


    // 七牛云配置
    config.qiniu = {
        AccessKey: process.env.QINIU_ACCESS_KEY, // 七牛云Access_Key
        SecretKey: process.env.QINIU_SECRET_KEY, // 七牛云SecretKey
    };

    // add your user config here
    const userConfig = {
        // myAppName: 'egg',
    };

    return {
        ...config,
        ...userConfig,
    };
};
